user3.row("–í–µ—Ä–Ω—É—Ç—Å—è –Ω–∞–∑–∞–¥")
@bot.message_handler(commands=['start'])

def start(message):
    bot.send_message(message.chat.id,"–ü—Ä–∏–≤–µ—Ç—Å—Ç–≤—É—é –≤ –∫–∏–Ω–æ—Ç–µ–∞—Ç—Ä–µ,–≤—ã–±–µ—Ä–∏—Ç–µ —á—Ç–æ —Ö–æ—Ç–∏—Ç–µ –ø–æ—Å–º–æ—Ç—Ä–µ—Ç—å.",reply_markup=menu)

@bot.message_handler(func=lambda message:True)
def second(message):
    if message.text == "–í–µ—Ä–Ω—É—Ç—å—Å—è –Ω–∞–∑–∞–¥":
        bot.send_message(message.chat.id, "–í–µ—Ä–Ω–µ–º—Å—è –Ω–∞–∑–∞–¥", reply_markup=menu)
    elif message.text in ["–ú–µ–ª–æ–¥—Ä–∞–º–º–∞ü•∞", "–ö–æ–º–µ–¥–∏—èüòÇ", "–£–∂–∞—Å—Ç–∏–∫üò±"]:
        if message.text == "–ú–µ–ª–æ–¥—Ä–∞–º–º–∞ü•∞":
            bot.send_message(message.chat.id, "–î–æ—Å—Ç—É–ø–Ω—ã–µ —Ñ–∏–ª—å–º—ã, –≤—ã–±–µ—Ä–∏—Ç–µ", reply_markup=user1)
        elif message.text == "–ö–æ–º–µ–¥–∏—èüòÇ":
            bot.send_message(message.chat.id, "–î–æ—Å—Ç—É–ø–Ω—ã–µ —Ñ–∏–ª—å–º—ã, –≤—ã–±–µ—Ä–∏—Ç–µ", reply_markup=user2)
        elif message.text == "–£–∂–∞—Å—Ç–∏–∫üò±":
            bot.send_message(message.chat.id, "–î–æ—Å—Ç—É–ø–Ω—ã–µ —Ñ–∏–ª—å–º—ã, –≤—ã–±–µ—Ä–∏—Ç–µ", reply_markup=user3)
    elif message.text in movies_info:
        movie_name = message.text
        movie_info = movies_info.get(movie_name)
        if movie_info:
            image_file_id = movie_info["image"]
            description = movie_info["description"]
            bot.send_photo(message.chat.id, image_file_id)
            bot.send_message(message.chat.id, description)

bot.polling(non_stop=True)









